package ArreglosOrdenamiento;

import java.lang.reflect.Array;
import java.util.Arrays;

public class conSort {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int arreglo[] = {5,3,2,99,10,12};
		
		//método del lenguaje para ordenar de menor a mayor
		Arrays.sort(arreglo);
	    
		//to String de la clase Arrays imprime el contenido del array.
	    System.out.print(Arrays.toString(arreglo));

	}

}
